package rsvier.user;import javax.persistence.*;import org.hibernate.annotations.Type;import org.springframework.context.annotation.Role;import org.springframework.context.annotation.Scope;import org.springframework.context.annotation.ScopedProxyMode;import org.springframework.stereotype.Component;import rsvier.address.Address;import java.io.Serializable;import java.util.Set;@Entity@Table(name = "users")@Component@Scope(proxyMode = ScopedProxyMode.TARGET_CLASS, value = "session")public class User implements Serializable {	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	private long id;	@Column(unique = true)	private String email;	private String passHash;	@Column(name = "type")	@Enumerated(EnumType.STRING)	private UserType userType;	@OneToOne(cascade = CascadeType.ALL)	private Address billingAddress;	@Column(nullable = false, columnDefinition = "TINYINT(1)")	private boolean enabled;	@ManyToMany(cascade = CascadeType.ALL)	@JoinTable(name = "user_role", joinColumns = @JoinColumn(name = "user_id"), inverseJoinColumns = @JoinColumn(name = "role_id"))	private Set<UserRole> roles;	public User() {	}	public User(User user) {		this.id = user.id;		this.email = user.email;		this.passHash = user.passHash;	}	public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public String getPassHash() {		return passHash;	}	public void setPassHash(String passHash) {		this.passHash = passHash;	}	public UserType getUserType() {		return userType;	}	public void setUserType(UserType userType) {		this.userType = userType;	}	public Address getBillingAddress() {		return billingAddress;	}	public void setBillingAdress(Address billingAddress) {		this.billingAddress = billingAddress;	}	public boolean isEnabled() {		return enabled;	}	public void setEnabled(boolean enabled) {		this.enabled = enabled;	}	public Set<UserRole> getRoles() {		return roles;	}	public void setRoles(Set<UserRole> roles) {		this.roles = roles;	}}